            + [{(COLUMNS[i+k], ROWS[j+k]) for k in range(4)}
               for i in range(len(COLUMNS) - 3) for j in range(len(ROWS) - 3)] \
            + [{(COLUMNS[i+k], ROWS[j-k]) for k in range(4)}
               for i in range(len(COLUMNS) - 3) for j in range(3, len(ROWS))]

    red_positions = set()
    yellow_positions = set()

    for position in pieces_positions:
        column, color = position.split("_")
        row = len([pos for pos in red_positions.union(yellow_positions) if pos[0] == column])
        if color == "Red":
            red_positions.add((column, row))
        else:
            yellow_positions.add((column, row))

        for line in LINES:
            if line.issubset(red_positions):
                return "Red"
            elif line.issubset(yellow_positions):
                return "Yellow"

    return "Draw"
