               for i in range(len(COLUMNS) - 3) for j in range(3, len(ROWS))]

    # initialize empty grid
    grid = [['' for _ in ROWS] for _ in COLUMNS]

    # iterate over pieces_positions
    for move in pieces_positions:
        # get column and color from move
        column, color = move.split('_')

        # find next available row in column
        for row in ROWS:
            if grid[COLUMNS.index(column)][row] == '':
                # assign color to the grid cell
                grid[COLUMNS.index(column)][row] = color
                break

    # check if there is a winner
    for line in LINES:
        cells = [grid[COLUMNS.index(column)][row] for column, row in line]
        if cells.count('Red') == 4:
            return 'Red'
        elif cells.count('Yellow') == 4:
            return 'Yellow'

    # check if the grid is full
    if all(all(cell != '' for cell in column) for column in grid):
        return 'Draw'

    # if there is no winner and the grid is not full, the game is ongoing
    return 'Ongoing'

