        elif cells.count('Yellow') == 4:
            return 'Yellow'

    # check if the grid is full
    if all(all(cell != '' for cell in column) for column in grid):
        return 'Draw'

    # if there is no winner and the grid is not full, the game is ongoing
    return 'Ongoing'

