        + [{(COLUMNS[i+k], ROWS[j-k]) for k in range(4)}
           for i in range(len(COLUMNS) - 3) for j in range(3, len(ROWS))]
    red_moves = set()
    yellow_moves = set()
    for move in pieces_positions:
        column, color = move.split('_')
        if color == 'Red':
            red_moves.add((column, ROWS[len(red_moves)]))
        elif color == 'Yellow':
            yellow_moves.add((column, ROWS[len(yellow_moves)]))
        if any(line <= red_moves for line in LINES):
            return 'Red'
        elif any(line <= yellow_moves for line in LINES):
            return 'Yellow'
    return 'Draw'

def who_is_winner(pieces_positions):
    COLUMNS, ROWS = 'ABCDEFG', range(6)
    LINES = [{(COLUMNS[i+k], ROWS[j]) for k in range(4)}
               for i in range(len(COLUMNS) - 3) for j in range(len(ROWS))] \
            + [{(COLUMNS[i], ROWS[j+k]) for k in range(4)}
               for i in range(len(COLUMNS)) for j in range(len(ROWS) - 3)] \
            + [{(COLUMNS[i+k], ROWS[j+k]) for k in range(4)}
               for i in range(len(COLUMNS) - 3) for j in range(len(ROWS) - 3)] \
            + [{(COLUMNS[i+k], ROWS[j-k]) for k in range(4)}
               for i in range(len(COLUMNS) - 3) for j in range(3, len(ROWS))]

    board = [[' ' for _ in range(7)] for _ in range(6)]

    for piece in pieces_positions:
        column, color = piece.split('_')
        column_index = COLUMNS.index(column)
        for row_index in range(5, -1, -1):
            if board[row_index][column_index] == ' ':
                board[row_index][column_index] = color
                break

    for line in LINES:
        line_values = set(board[row][COLUMNS.index(column)] for column, row in line)
        if line_values == {'Red'}:
            return 'Red'
        elif line_values == {'Yellow'}:
            return 'Yellow'

    return 'Draw'
