# Implementation of the Extended Euclidean Algorithm
# to find the modular multiplicative inverse of a modulo m

def egcd(a, b):
    if a == 0:
        return (b, 0, 1)
    else:
        g, y, x = egcd(b % a, a)
        return (g, x - (b // a) * y, y)

def inverseMod(a, m):
    # Check if a and m are coprime
    g, x, y = egcd(a, m)
    if g != 1:
        return None

    # Return the modular multiplicative inverse of a modulo m
    return x % m

