def validate(n):
    # Convert the number to a string
    n = str(n)

    # Reverse the string
    n = n[::-1]

    # Convert the string to a list of integers
    digits = [int(x) for x in n]

    # Double every other digit starting from the second digit
    doubled_digits = []
    for i in range(len(digits)):
        if i % 2 == 1:
            doubled_digits.append(digits[i] * 2)
        else:
            doubled_digits.append(digits[i])

    # Subtract 9 from any digits greater than 9
    final_digits = []
    for digit in doubled_digits:
        if digit > 9:
            final_digits.append(digit - 9)
        else:
            final_digits.append(digit)

    # Calculate the sum of the final digits
    total = sum(final_digits)

    # Check if the total is divisible by 10
    if total % 10 == 0:
        return True
    else:
        return False

