def validate(n):
    # Convert the number to a string
    n = str(n)

    # Check if the number is of even length
    if len(n) % 2 == 0:
        even_length = True
    else:
        even_length = False

    # Create a list to store the doubled digits
    doubled_digits = []

    # Loop through each digit of the number
    for i in range(len(n)):
        # Get the digit at the current position
        digit = int(n[i])

        # Double the digit if it is at an even position
        if even_length:
            if i % 2 == 0:
                digit *= 2
        else:
            if i % 2 == 1:
                digit *= 2

        # If the doubled digit is greater than 9, subtract 9 from it
        if digit > 9:
            digit -= 9

        # Append the doubled digit to the list
        doubled_digits.append(digit)

    # Calculate the sum of all digits
    digit_sum = sum(doubled_digits)

    # Check if the sum is divisible by 10
    if digit_sum % 10 == 0:
        return True
    else:
        return False

