def validate(n):
    digits = [int(x) for x in str(n)]

    if len(digits) % 2 == 0:
        double_digits = [2*x if i % 2 == 0 else x for i, x in enumerate(digits)]
    else:
        double_digits = [x if i % 2 == 0 else 2*x for i, x in enumerate(digits)]

    sum_digits = [x if x <= 9 else x-9 for x in double_digits]

    total_sum = sum(sum_digits)

    return total_sum % 10 == 0
