    # Convert the integer to a string to be able to iterate over its digits
    digits = str(n)

    # If the number of digits is odd, we start doubling from the second digit
    if len(digits) % 2 != 0:
        start = 1
    else:
        start = 0

    # Iterate over the digits, doubling every other digit starting from start
    doubled_digits = []
    for i in range(len(digits)):
        # Convert the digit to an integer
        digit = int(digits[i])
        # Double the digit if it should be doubled
        if i % 2 == start:
            doubled_digit = digit * 2
            # If the doubled digit is greater than 9, subtract 9 from it
            if doubled_digit > 9:
                doubled_digit -= 9
            doubled_digits.append(doubled_digit)
        else:
            doubled_digits.append(digit)

    # Sum all of the doubled digits
    sum_digits = sum(doubled_digits)

    # Check if the sum is divisible by 10
    if sum_digits % 10 == 0:
        return True
    else:
        return False

