def validate(n):
    n = str(n)
    odd_sum = 0
    for i, digit in enumerate(reversed(n)):
        if i % 2 != 0:
            digit = str(int(digit) * 2)
            if int(digit) > 9:
                digit = str(sum(int(x) for x in digit))
        odd_sum += int(digit)
    return odd_sum % 10 == 0

