                    digits[i] = digits[i] - 9
    else:
        for i in range(len(digits)):
            if i % 2 != 0:
                digits[i] = digits[i] * 2
                if digits[i] > 9:
                    digits[i] = digits[i] - 9
    if sum(digits) % 10 == 0:
        return True
    else:
        return False
