def validate(n):
    n = n.replace(' ', '')
    check = lambda a: int(a) if a.isdigit() else 0
    digits = list(map(check, n[::-1]))
    for i in range(len(digits)-2,-1,-2):
        digits[i] *= 2
        if digits[i] > 9:
            digits[i] -= 9
    return sum(digits) % 10 == 0

