def zombie_shootout(zombies, distance, ammo, shot=0):
    # Calculate the time it takes for the zombies to reach the player
    time_to_reach = distance / 0.5

    # Calculate the maximum number of zombies that can be shot
    max_zombies_shot = min(ammo, zombies)

    # If there are no zombies or no ammo, return the appropriate message
    if zombies == 0:
        return f"You shot all {shot} zombies."
    elif ammo == 0:
        return f"You shot {shot} zombies before being eaten: ran out of ammo."

    # If the time to reach is less than or equal to the number of zombies shot,
    # the player is overwhelmed and gets eaten
    if time_to_reach <= shot:
        return f"You shot {shot} zombies before being eaten: overwhelmed."

    # If the number of zombies shot is equal to the maximum possible, the player
    # has shot all the zombies and wins
    if shot == max_zombies_shot:
        return f"You shot all {max_zombies_shot} zombies."

    # Recursively call the function with updated parameters
    return zombie_shootout(zombies - 1, distance, ammo - 1, shot + 1)

