def zombie_shootout(zombies, distance, ammo, shot=0):
    while shot < ammo and distance > 0:
        distance -= 0.5
        if distance <= 0:
            return "You shot {} zombies before being eaten: overwhelmed.".format(shot + zombies - (ammo - shot))
        shot += 1
        distance -= 0.5 * zombies
        zombies -= 1
    if shot == ammo:
        return "You shot all {} zombies.".format(shot)
    else:
        return "You shot {} zombies before being eaten: ran out of ammo.".format(shot)

