def zombie_shootout(zombies, distance, ammo, shot=0):
    while zombies > 0:
        zombies -= 1
        ammo -= 1
        distance -= 0.5
        if distance <= 0:
            if ammo >= zombies:
                return f"You shot {shot + zombies} zombies before being eaten: overwhelmed."
            else:
                return f"You shot {shot + ammo} zombies before being eaten: ran out of ammo."
        else:
            shot += 1

    return f"You shot all {shot} zombies."

def zombie_shootout(zombies, distance, ammo, shot=0):
    if zombies == 0:
        return f"You shot all {shot} zombies."
    if distance == 0:
        return f"You shot {shot} zombies before being eaten: overwhelmed."
    if ammo == 0:
