    chord_notes = chord.split()

    if len(chord_notes) != 3:
        return 'Not a chord'

    indices = [flat_notes.index(note) for note in chord_notes if note in flat_notes]

    if len(indices) != 3:
        return 'Not a chord'

    interval1 = (indices[1] - indices[0]) % 12
    interval2 = (indices[2] - indices[1]) % 12

    if interval1 == 3 and interval2 == 4:
        return 'Minor'
    elif interval1 == 4 and interval2 == 3:
        return 'Major'
    else:
        return 'Not a chord'
