    total_interval = third - first

    if total_interval < 0:
        total_interval += 12
    if interval1 < 0:
        interval1 += 12
    if interval2 < 0:
        interval2 += 12

    if interval1 == 3 and interval2 == 4 and total_interval == 7:
        return "Minor"
    elif interval1 == 4 and interval2 == 3 and total_interval == 7:
        return "Major"
    else:
        return "Not a chord"
