def minor_or_major(chord):
    notes = ['C', ['C#', 'Db'], 'D', ['D#', 'Eb'], 'E', 'F', ['F#', 'Gb'], 'G', ['G#', 'Ab'], 'A', ['A#', 'Bb'], 'B']
    chord_notes = chord.split()

    if len(chord_notes) != 3:
        return 'Not a chord'

    # Function to find the index of a note in the chromatic scale
    def find_index(note):
        for i, n in enumerate(notes):
            if note in n:
                return i
        return -1

    # Find indices of the notes in the chord
    indices = [find_index(note) for note in chord_notes]

    if -1 in indices:
        return 'Not a chord'

    interval1 = indices[1] - indices[0]
    interval2 = indices[2] - indices[1]

    if interval1 < 0:
        interval1 += 12
    if interval2 < 0:
        interval2 += 12

    if interval1 == 3 and interval2 == 4:
        return 'Minor'
    elif interval1 == 4 and interval2 == 3:
        return 'Major'

    return 'Not a chord'
