    chord_notes = chord.split()

    if len(chord_notes) != 3:
        return "Not a chord"

    positions = []
    for note in chord_notes:
        found = False
        for i, n in enumerate(notes):
            if note in (n if isinstance(n, list) else [n]):
                positions.append(i)
                found = True
                break
        if not found:
            return "Not a chord"

    interval1 = (positions[1] - positions[0]) % 12
    interval2 = (positions[2] - positions[1]) % 12

    if interval1 == 3 and interval2 == 4:
        return "Minor"
    elif interval1 == 4 and interval2 == 3:
        return "Major"
    else:
        return "Not a chord"
