    flat_notes = [n if isinstance(n, str) else n[0] for n in notes]
    notes_dict = {note: i for i, note in enumerate(flat_notes)}

    chord_elements = chord.split()
    if len(chord_elements) != 3:
        return 'Not a chord'

    try:
        intervals = [notes_dict[chord_elements[i]] - notes_dict[chord_elements[i - 1]] for i in range(1, 3)]
    except KeyError:
        return 'Not a chord'

    if intervals == [3, 4]:
        return 'Minor'
    elif intervals == [4, 3]:
