        return 'Not a chord'

    intervals = []

    for i in range(2):
        first_note_index = notes.index(chord_notes[i])
        second_note_index = notes.index(chord_notes[i + 1])
        interval = (second_note_index - first_note_index) % 12
        intervals.append(interval)

    total_interval = (notes.index(chord_notes[2]) - notes.index(chord_notes[0])) % 12

    if intervals == [3, 4] and total_interval == 7:
        return 'Minor'
    elif intervals == [4, 3] and total_interval == 7:
        return 'Major'
    else:
        return 'Not a chord'
