def minor_or_major(chord):
    notes = ['C', 'C#', 'D', 'D#', 'E', 'F', 'F#', 'G', 'G#', 'A', 'A#', 'B']
    chord_notes = chord.split()

    if len(chord_notes) != 3:
        return "Not a chord"

    try:
        indexes = [notes.index(note) for note in chord_notes]
    except ValueError:
        return "Not a chord"

    interval1 = (indexes[1] - indexes[0]) % 12
    interval2 = (indexes[2] - indexes[1]) % 12

    if interval1 == 3 and interval2 == 4:
        return "Minor"
    elif interval1 == 4 and interval2 == 3:
        return "Major"
    else:
        return "Not a chord"
