def solve(a, b):
    from collections import defaultdict
    from fractions import Fraction

    harmonic = [0] + [Fraction(sum({y for x in range(1, int(n**0.5) + 1) for y in [x, n // x] if not n % x}), n) for n in range(1, 2001)]

    harmonicity = defaultdict(set)
    for n, h in enumerate(harmonic): 
        harmonicity[h].add(n)

    HARMO_GROUPS = {h: sorted(s) for h, s in harmonicity.items() if len(s) > 1}

    lowest_sum = 0
    found_pairs = set()

    for members in HARMO_GROUPS.values():
        for i in range(len(members)):
            for j in range(i + 1, len(members)):
                if a <= members[i] < b and a <= members[j] < b:
                    found_pairs.add((min(members[i], members[j]), max(members[i], members[j])))

    lowest_sum = sum(pair[0] for pair in found_pairs)

    return lowest_sum if found_pairs else 0
