def solve(a, b):
    from collections import defaultdict
    from fractions import Fraction

    harmonic = [0] + [Fraction(sum({y for x in range(1, int(n**.5) + 1) for y in [x, n // x] if not n % x}), n) for n in range(1, 2001)]

    harmonicity = defaultdict(set)
    for n, h in enumerate(harmonic): 
        harmonicity[h].add(n)

    HARMO_GROUPS = {h: sorted(s) for h, s in harmonicity.items() if len(s) > 1}

    pairs_sum = 0
    found_pairs = set()

    for group in HARMO_GROUPS.values():
        for i in range(len(group)):
            for j in range(i + 1, len(group)):
                a1, a2 = group[i], group[j]
                if a <= a1 < b and a <= a2 < b:
                    found_pairs.add((min(a1, a2), max(a1, a2)))

    for pair in found_pairs:
        pairs_sum += pair[0]

    return pairs_sum if pairs_sum > 0 else 0
