def solve(a, b):
    harmonic = [0] + [Fraction(sum({y for x in range(1,int(n**0.5)+1) for y in [x, n//x] if not n%x}), n) for n in range(1,2001)]
    harmonicity = defaultdict(set)
    for n,h in enumerate(harmonic): harmonicity[h].add(n)
    harmonicity = defaultdict(set, ((k,sorted([n for n in s])) for k,s in harmonicity.items()))
    result = 0
    seen = set()
    for n in range(a, b+1):
        ratio = Fraction(n, n).limit_denominator()
        for k in sorted(harmonicity.keys()):
            while k <= ratio and ratio in harmonicity:
                if n not in seen:
                    result += n
                    seen.add(n)
                ratio //= harmonicity[k][0]
            if ratio not in harmonicity:
                break
    return result

