def solve(a, b):
    harmony = [0] + [Fraction(sum({y for x in range(1,int(n**0.5)+1) for y in [x, n//x] if not n%x}), n) for n in range(1,2001)]
    harmonicity = defaultdict(set)
    for n,h in enumerate(harmony): harmonicity[h].add(n)
    groups = {h: sorted(s) for h,s in harmonicity.items() if len(s) > 1}
    ratios = {n: h for h,lst in groups.items() for n in lst}
    num = sorted(ratios.keys())
    total = 0
    for i in range(len(num)):
        total += num[i]
        if i > 0 and num[i] / num[i-1] < 1:
            break
    return total if total <= b else 0

