    for n in range(a, b + 1):
        harmonic_divisors = [i for i in range(1, n + 1) if n % i == 0]
        harmonic_ratio = Fraction(sum(harmonic_divisors), n)
        harmonicity[harmonic_ratio].append(n)
    result = []
    for ratio, group in harmonicity.items():
        if len(group) > 1:
            result.append(min(group))
    return sum(result) if result else 0

