    from collections import defaultdict
    from fractions import Fraction

    def sum_of_divisors(n):
        total = 0
        for i in range(1, int(n**0.5) + 1):
            if n % i == 0:
                total += i
                if i != n // i:
                    total += n // i
        return total

    harmonicity = defaultdict(list)

    for n in range(a, b):
        sum_div = sum_of_divisors(n)
        if n > 0:
            ratio = Fraction(sum_div, n)
            harmonicity[ratio].append(n)

    lowest_sum = 0
    found_pair = False

    for members in harmonicity.values():
        if len(members) > 1:
            found_pair = True
            lowest_sum += min(members)

    return lowest_sum if found_pair else 0
