def solve(a, b):
    from collections import defaultdict
    from fractions import Fraction

    harmonic = [0] + [Fraction(sum({y for x in range(1,int(n**.5)+1) for y in [x, n//x] if not n%x}), n) for n in range(1, 2001)]

    harmonicity = defaultdict(set)
    for n, h in enumerate(harmonic): 
        harmonicity[h].add(n)

    HARMO_GROUPS = {h: sorted(s) for h, s in harmonicity.items() if len(s) > 1}

    total_sum = 0
    found_pairs = set()

    for members in HARMO_GROUPS.values():
        pairs = [(x, y) for i, x in enumerate(members) for y in members[i + 1:] if a <= x < b and a <= y < b]
        for x, y in pairs:
            if (x, y) not in found_pairs and (y, x) not in found_pairs:
                found_pairs.add((x, y))
                total_sum += x

    return total_sum if found_pairs else 0
