    harmonicity = {}

    for n in range(a, b):
        if n > 0:
            ratio = harmonic[n] / n
            if ratio not in harmonicity:
                harmonicity[ratio] = []
            harmonicity[ratio].append(n)

    lowest_members_sum = 0
    has_pairs = False

    for members in harmonicity.values():
        if len(members) > 1:
            has_pairs = True
            lowest_members_sum += min(members)

    return lowest_members_sum if has_pairs else 0
