def nth_perm(n, d):
    if n == 1:
        return ''.join(map(str, range(d)))
    fact = math.factorial(d - 1)
    n -= 1
    digits = list(map(str, range(d)))
    result = []
    for i in range(d - 1, 0, -1):
        pos, n = divmod(n, fact)
        result.append(digits.pop(pos))
    result.append(digits[0])
    return ''.join(result)
