def nth_perm(n, d):
    digits = [str(i) for i in range(d)]
    result = []

    for i in range(d, 0, -1):
        fact = math.factorial(i-1)
        index = (n-1) // fact
        result.append(digits.pop(index))
        n = (n-1) % fact + 1

    return "".join(result)
