import math
def nth_perm(n, d):
    digits = list(range(d))
    result = []
    for i in range(d-1, -1, -1):
        div = math.factorial(i)
        index = n // div
        n %= div
        result.append(digits.pop(index))
    return ''.join(str(d) for d in result)

