def nth_perm(n,d):
    def factorial(n):
        if n == 0:
            return 1
        else:
            return n * factorial(n-1)

    digits = [str(i) for i in range(d)]
    perm = ''
    n -= 1

    for i in range(d, 0, -1):
        index = n // factorial(i-1)
        perm += digits[index]
        digits.remove(digits[index])
        n = n % factorial(i-1)

    return perm

