def correct(m, n, bits):

    # Create a 2D array to store the bits
    message = [[0 for i in range(n)] for j in range(m)]

    # Separate the message bits, row parity bits, and column parity bits
    message_bits = bits[:m*n]
    row_parity_bits = bits[m*n:m*n+m]
    column_parity_bits = bits[m*n+m:]

    # Fill the message array with the message bits
    for i in range(m):
        for j in range(n):
            message[i][j] = int(message_bits[i*n+j])

    # Calculate the row and column parities
    row_parity = [0 for i in range(m)]
    column_parity = [0 for i in range(n)]

    for i in range(m):
        for j in range(n):
            row_parity[i] += message[i][j]
            column_parity[j] += message[i][j]

    for i in range(m):
        row_parity[i] %= 2

    for j in range(n):
        column_parity[j] %= 2

    # Check for errors in the row parity bits
    row_error = -1

    for i in range(m):
        if row_parity_bits[i] != str(row_parity[i]):
            if row_error != -1:
                return bits  # More than one error, cannot correct
            row_error = i

    # Check for errors in the column parity bits
    column_error = -1

    for j in range(n):
        if column_parity_bits[j] != str(column_parity[j]):
            if column_error != -1:
                return bits  # More than one error, cannot correct
            column_error = j

    # Correct the error
    if row_error != -1 and column_error != -1:
        message[row_error][column_error] ^= 1

    # Recalculate the row and column parities
    for i in range(m):
        row_parity[i] = sum(message[i]) % 2

    for j in range(n):
        column_parity[j] = sum([message[i][j] for i in range(m)]) % 2

    # Update the parity bits in the message
    for i in range(m):
        message_bits = message_bits[:i*n] + ''.join([str(b) for b in message[i]]) + message_bits[(i+1)*n:]

