        if str(col_count) != col_parity[i]:
            col_error = i + 1

    if row_error > 0 and col_error > 0:
        corrected_message[(row_error-1)*n + col_error-1] = str(1 - int(corrected_message[(row_error-1)*n + col_error-1]))
    elif row_error > 0:
        row_parity = list(row_parity)
        row_parity[row_error-1] = str(1 - int(row_parity[row_error-1]))
        row_parity = ''.join(row_parity)
    elif col_error > 0:
        col_parity = list(col_parity)
        col_parity[col_error-1] = str(1 - int(col_parity[col_error-1]))
        col_parity = ''.join(col_parity)

    return ''.join(corrected_message) + row_parity + col_parity
