def correct(m, n, bits):
    bits = list(bits)
    data = [bits[i * n:(i + 1) * n] for i in range(m)]
    row_parities = bits[m * n:m * n + m]
    col_parities = bits[m * n + m:m * n + m + n]

    error_row = -1
    error_col = -1

    # Check row parities
    for i in range(m):
        if sum(int(bit) for bit in data[i]) % 2 != int(row_parities[i]):
            if error_row == -1:
                error_row = i
            else:
                error_row = -2  # More than one error

    # Check column parities
    for j in range(n):
        col_sum = sum(int(data[i][j]) for i in range(m))
        if col_sum % 2 != int(col_parities[j]):
            if error_col == -1:
                error_col = j
            else:
                error_col = -2  # More than one error

    # If exactly one error is detected, correct it
    if error_row >= 0 and error_col >= 0:
        index = error_row * n + error_col
        bits[index] = '1' if bits[index] == '0' else '0'

    return ''.join(bits)
