def correct(m, n, bits):
    total_bits = m * n
    message = [list(map(int, bits[i:i+n])) for i in range(0, total_bits, n)]
    row_parity = list(map(int, bits[total_bits:total_bits + m]))
    col_parity = list(map(int, bits[total_bits + m:total_bits + m + n]))

    row_errors = [sum(row) % 2 for row in message]
    col_errors = [sum(message[i][j] for i in range(m)) % 2 for j in range(n)]

    row_error_index = -1
    col_error_index = -1

    for i in range(m):
        if row_parity[i] != row_errors[i]:
            row_error_index = i
            break

    for j in range(n):
        if col_parity[j] != col_errors[j]:
            col_error_index = j
            break

    if row_error_index != -1 and col_error_index != -1:
        message[row_error_index][col_error_index] ^= 1

    corrected_bits = ''.join(''.join(map(str, row)) for row in message)
    return corrected_bits + ''.join(map(str, row_errors)) + ''.join(map(str, col_errors))
