def correct(m, n, bits):
    msg = bits[:m*n]
    row_parities = bits[m*n:m*n+m]
    col_parities = bits[m*n+m:]

    # Check if there is an error in the message
    for i in range(m):
        row_sum = sum((1 & int(msg[j*(n+1)+i])) for j in range(n))
        if row_parities[i] != str(row_sum % 2):
            for j in range(n):
                if (1 & int(msg[j*(n+1)+i])) ^ row_parities[i] == 1:
                    msg = msg[:j*(n+1)+i] + '0' if msg[j*(n+1)+i] == '1' else '1' + msg[j*(n+1)+i+1:]
                    break

    for j in range(n):
        col_sum = sum((1 & int(msg[i*(n+1)+j])) for i in range(m))
        if col_parities[j] != str(col_sum % 2):
            for i in range(m):
                if (1 & int(msg[i*(n+1)+j])) ^ col_parities[j] == 1:
                    msg = msg[:i*(n+1)+j] + '0' if msg[i*(n+1)+j] == '1' else '1' + msg[i*(n+1)+j+1:]
                    break

    return msg + ''.join(row_parities) + ''.join(col_parities)
