def snail(column, day, night):
    day_progress = day
    night_progress = -night
    day_night = 0
    while column > 0:
        day_progress += day
        day_night += 1
        if day_progress >= column:
            column -= day_progress
            day_progress = night_progress
        else:
            day_progress = day
    return day_night - 1

