def sort_string(s, ordering):
    order_map = {char: index for index, char in enumerate(dict.fromkeys(ordering))}
    sorted_chars = sorted(s, key=lambda x: (order_map.get(x, float('inf')), s.index(x)))
    return ''.join(sorted_chars)
