def sort_string(s, ordering):
    order_dict = {char: index for index, char in enumerate(ordering)}
    sorted_ordering = sorted(set(ordering), key=lambda x: order_dict[x])
    order_index = {char: i for i, char in enumerate(sorted_ordering)}

    in_order = []
    out_of_order = []

    for char in s:
        if char in order_index:
            in_order.append(char)
        else:
            out_of_order.append(char)

    in_order.sort(key=lambda x: order_index[x] if x in order_index else float('inf'))

    return ''.join(in_order) + ''.join(out_of_order)
