def siegfried(week, txt):
    if week < 1 or week > 5:
        return txt

    txt = txt.replace('CI', 'SI').replace('ci', 'si').replace('CE', 'SE').replace('ce', 'se').replace('C', 'k').replace('c', 'k')
    if week > 1:
        txt = txt.replace('PH', 'F').replace('ph', 'f')
    if week > 2:
        words = txt.split()
        for i in range(len(words)):
            if len(words[i]) > 3:
                words[i] = words[i].rstrip('e')
            words[i] = ''.join(sorted(set(words[i]), key=words[i].index))
        txt = ' '.join(words)
    if week > 3:
        txt = txt.replace('TH', 'Z').replace('th', 'z').replace('WR', 'R').replace('wr', 'r').replace('WH', 'V').replace('wh', 'v').replace('W', 'V').replace('w', 'v')
    if week > 4:
        txt = txt.replace('OU', 'U').replace('ou', 'u').replace('AN', 'UN').replace('an', 'un')
        words = txt.split()
        for i in range(len(words)):
            if words[i].endswith('ING'):
                words[i] = words[i][:-3] + 'ink'
            if words[i].startswith('SM'):
                words[i] = 'schm' + words[i][2:]
        txt = ' '.join(words)

    return txt
