def siegfried(week, txt):
    patterns = [re.compile(r'(?i)ci|ce|c(?!h)'),
                re.compile(r'(?i)ph'),
                re.compile(r'(?i)(?<!\b[a-z]{1})(?<!\b[a-z]{2})e\b|([a-z])\1'),
                re.compile(r'(?i)th|w[rh]?'),
                re.compile(r'(?i)ou|an|ing\b|\bsm')]

    changes = {"ci": "si", "ce": "se", "c":"k", 
               "ph": "f", 
               "th": "z",  "wr": "r",  "wh":  "v",   "w": "v", 
               "ou": "u",  "an": "un", "ing": "ink", "sm": "schm"}

    for p, c in zip(patterns, changes.values()):
        txt = p.sub(lambda m: change(m), txt)

    if week == 1:
        return txt
    elif week == 2:
        return txt.lower()
    elif week == 3:
        return txt.capitalize()
    elif week == 4:
        return txt.lower().strip().replace("ze", "the").replace("fifz", "five").replace("viz", "with").replace("un", "an")
    elif week == 5:
        return txt.lower().replace("schmiles", "smiles").replace("unaksent", "unaccented").replace("siegfried", "Siegfried")
    else:
        return "Invalid week number. Please enter a number between 1 and 5."

