        [("th", "z"), ("wr", "r"), ("wh", "v"), ("w", "v")],
        [("ou", "u"), ("an", "un"), ("ing$", "ink"), ("^sm", "schm")]
    ]

    def apply_rules(text, week):
        for pattern, replacement in rules[week - 1]:
            text = re.sub(pattern, replacement, text, flags=re.IGNORECASE)
        return text

    txt = apply_rules(txt, 1)
    if week >= 2:
        txt = apply_rules(txt, 2)
    if week >= 3:
        txt = re.sub(r'e\b(?!\s*\w{1,2}\b)', '', txt)
        txt = re.sub(r'(.)\1', r'\1', txt)
    if week >= 4:
        txt = apply_rules(txt, 4)
    if week >= 5:
        txt = apply_rules(txt, 5)

    return txt
