            return "schm"
        elif tok.lower() == 'ing' and m.end() == len(txt):
            return "ink"
        else:
            return CHANGES.get(tok.lower(), tok)  # Return the original word if no change needed

    txt = PATTERNS[4].sub(change_week5, txt)

