def siegfried(week, txt):
    if week < 1 or week > 5:
        return txt

    txt = txt.replace('CI', 'SI').replace('ci', 'si').replace('CE', 'SE').replace('ce', 'se').replace('c', 'k')
    txt = txt.replace('ph', 'f')

    if week >= 3:
        words = txt.split()
        for i in range(len(words)):
            if len(words[i]) > 3:
                words[i] = words[i].rstrip('e')
            if len(words[i]) > 1:
                words[i] = re.sub(r'(.)\1', r'\1', words[i])
        txt = ' '.join(words)

    txt = txt.replace('th', 'z').replace('TH', 'Z').replace('wr', 'r').replace('WR', 'R').replace('wh', 'v').replace('WH', 'V').replace('w', 'v').replace('W', 'V')

    if week >= 5:
        words = txt.split()
        for i in range(len(words)):
            if words[i].endswith('ou'):
                words[i] = words[i][:-2] + 'u'
            if words[i].endswith('an'):
                words[i] = words[i][:-2] + 'un'
            if words[i].endswith('ing'):
                words[i] = words[i][:-3] + 'ink'
            if i == 0 and words[i].startswith('sm'):
                words[i] = 'schm' + words[i][2:]
        txt = ' '.join(words)

    return txt
