    txt = re.sub(r'(.)\1+', r'\1', txt)

    if week < 4:
        return txt

    # Week 4
    txt = PATTERNS[3].sub(change, txt)

    if week < 5:
        return txt

    # Week 5
    def week5_change(m):
        word = m.group(0)
        if word.lower().startswith("sm"):
            return "schm" + word[2:]
        elif word.lower().endswith("ing"):
            return word[:-3] + "ink"
        return CHANGES.get(word.lower(), word)

    txt = PATTERNS[4].sub(week5_change, txt)

    return txt
