                return "schm"
            return m.group(0)

        txt = re.sub(r'(?i)\bsm', replace_sm, txt)
        txt = PATTERNS[4].sub(change, txt)
        txt = re.sub(r'(?<!\b[a-z]{1})(?<!\b[a-z]{2})e\b', '', txt)  # handle 'e' again for week 5

