    if week >= 3:
        txt = re.sub(PATTERNS[2], change, txt)

    if week >= 4:
        txt = re.sub(PATTERNS[3], change, txt)

    if week == 5:
        def replace_endings(m):
            word = m.group(0)
            if word.lower().startswith("sm"):
                return "schm" + word[2:]
            if word.lower().endswith("ing"):
                return word[:-3] + "ink"
            return word

        txt = re.sub(r'\bsm\w*|\w+ing\b', replace_endings, txt)
        txt = re.sub(PATTERNS[4], change, txt)

    return txt
